// Minified JavaScript for the ballot paper form in the election_stv module.
(function($){Drupal.behaviors.election_vote={attach:function(d,e){var f=$('.election-candidate-preference-select',d),disableOptions=function(a,b){var c=a.val(),prev=a.data('previous_value');if(prev!==undefined){b.find('option[value="'+prev+'"][disabled]').removeAttr('disabled')}if(c!==''&&c!=='NONE'){b.find('option[value="'+c+'"]').not(':selected').attr('disabled','disabled')}a.data('previous_value',c)};f.each(function(){var a=$(this),others=f.not(a),allow_equal=a.closest('form').hasClass('allow-equal');if(!allow_equal){disableOptions(a,others);a.change(function(){disableOptions(a,others)})}})}}}(jQuery));
